const http = require('http')

http.createServer((req, res) => {
    console.log(req.method, req.url)

    if(req.url === '/user/1') {
        const user = {
            name: 'zhangsan',
            age: '18',
            method: req.method
        }
  
        res.writeHead(200, {'content-type': 'text/json'})
        res.write(JSON.stringify(user))
    } else {
        res.writeHead(404)
    }

    res.end()
}).listen(3000)

console.log("Server is running, listening port 3000 ...")