import ApplicationContext from '../ApplicationContext'

type OperatorType = 'Method' | 'Property' | 'Parameter' | 'Bean'

export default class Log {
    static isLog() {
        return ApplicationContext.getInstance().getBeanFactory().isLog()
    }

    static info(type: OperatorType, message: string) {
        const reg = /\d\d:\d\d:\d\d/g
        const time = (new Date().toString().match(reg) as Array<string>)[0]
        console.log(`[${time}] [Info] [${type}] ${message}`)
    }
}