import ApplicationContext, { Get, PathVariable, Property, ReturnType } from '../src'
import View from '../src/view/annotations/View'

ApplicationContext.getInstance({log: true})

class User {
  @Property('username')
      name?: string
}

class UserMapper {
  @Get('/{id}')
  @ReturnType('User')
    static getUser(@PathVariable('id') id: number) {}
}

